import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      person: {
        fullName: 'John Doe',
        bio: 'Lorem ipsum dolor sit amet',
        imgSrc: 'path/to/image',
        profession: 'Software Engineer'
      },
      shows: false,
      intervalId: null,
      mountedTime: null
    };
  }

  componentDidMount() {
    this.setState({ mountedTime: new Date() });

    // Start the interval to update the time interval
    const intervalId = setInterval(() => {
      this.setState({ mountedTime: new Date() });
    }, 1000);
    this.setState({ intervalId });
  }

  componentWillUnmount() {
    // Clear the interval to prevent memory leaks
    clearInterval(this.state.intervalId);
  }

  toggleShow = () => {
    this.setState((prevState) => ({
      shows: !prevState.shows
    }));
  }

  render() {
    const { person, shows, mountedTime } = this.state;

    return (
      <div>
        <button onClick={this.toggleShow}>Toggle Show</button>
        {shows && (
          <div>
            <h1>{person.fullName}</h1>
            <p>{person.bio}</p>
            <img src={person.imgSrc} alt="Person" />
            <p>Profession: {person.profession}</p>
          </div>
        )}
        <p>Time interval since component mounted: {mountedTime && (new Date() - mountedTime) / 1000} seconds</p>
      </div>
    );
  }
}

export default App;
