        <h3> {this.state.bio}</h3>;
        <h3> {this.state.profession}</h3>;
      </>
    )
  }*/
  componentDidMount 

  render() {
    return (
      <>
      <Button variant="primary" onClick={this.toggle} >show</Button>
      { this.state.shows ? 
        (<><img src={this.state.imgSrc} alt="person" />
          <h3> {this.state.fullName}</h3>
          <h3> {this.state.bio}</h3>
          <h3> {this.state.profession}</h3>
          </>): null }
      </>
    )
  }
}
